#import "AGCCorePlugin.h"
#import <Cordova/CDV.h>
#import <AGConnectCore/AGConnectCore.h>

@implementation AGCCorePlugin

- (void)setApiKey:(CDVInvokedUrlCommand*)command {
    [self.commandDelegate runInBackground:^{
        NSLog(@"The setApiKey method only supports Android, please refer to the development guide to set the apiKey on iOS.");
        [self.commandDelegate sendPluginResult:[CDVPluginResult resultWithStatus:CDVCommandStatus_OK] callbackId:command.callbackId];
    }];
}

- (void)setClientId:(CDVInvokedUrlCommand*)command {
    [self.commandDelegate runInBackground:^{
        NSLog(@"The setClientId method only supports Android, please refer to the development guide to set the clientId on iOS.");
        [self.commandDelegate sendPluginResult:[CDVPluginResult resultWithStatus:CDVCommandStatus_OK] callbackId:command.callbackId];
    }];
}

- (void)setClientSecret:(CDVInvokedUrlCommand*)command {
    [self.commandDelegate runInBackground:^{
        NSLog(@"The setClientSecret method only supports Android, please refer to the development guide to set the clientSecret on iOS.");
        [self.commandDelegate sendPluginResult:[CDVPluginResult resultWithStatus:CDVCommandStatus_OK] callbackId:command.callbackId];
    }];
}

@end
