var exec = require('cordova/exec');

class AGCCorePlugin {
    asyncExecute(action, param) {
        return new Promise((resolve, reject) => {
            exec(resolve, reject, 'AGCCorePlugin', action, (param === undefined || param === null) ? [] : param);
        });
    }

    setApiKey(apiKey) {
        return this.asyncExecute('setApiKey', [apiKey]);
    }

    setClientId(clientId) {
        return this.asyncExecute('setClientId', [clientId]);
    }

    setClientSecret(clientSecret) {
        return this.asyncExecute('setClientSecret', [clientSecret]);
    }
}

module.exports = new AGCCorePlugin();

